if(process.env.OBJECT_IMPL){global.TYPED_ARRAY_SUPPORT=false}var B=require("../").Buffer;var test=require("tape");test("write/read Infinity as a float",function(b){var a=new B(4);b.equal(a.writeFloatBE(Infinity,0),4);b.equal(a.readFloatBE(0),Infinity);b.end()});test("write/read -Infinity as a float",function(b){var a=new B(4);b.equal(a.writeFloatBE(-Infinity,0),4);b.equal(a.readFloatBE(0),-Infinity);b.end()});test("write/read Infinity as a double",function(b){var a=new B(8);b.equal(a.writeDoubleBE(Infinity,0),8);b.equal(a.readDoubleBE(0),Infinity);b.end()});test("write/read -Infinity as a double",function(b){var a=new B(8);b.equal(a.writeDoubleBE(-Infinity,0),8);b.equal(a.readDoubleBE(0),-Infinity);b.end()});test("write/read float greater than max",function(b){var a=new B(4);b.equal(a.writeFloatBE(4e+38,0),4);b.equal(a.readFloatBE(0),Infinity);b.end()});test("write/read float less than min",function(b){var a=new B(4);b.equal(a.writeFloatBE(-4e+40,0),4);b.equal(a.readFloatBE(0),-Infinity);b.end()});