var test=require("tape");var algorithms=["sha1","sha224","sha256","sha384","sha512","md5","rmd160","ripemd160"];var encodings=["hex","base64"];var vectors=require("hash-test-vectors");vectors.forEach(function(a){a.ripemd160=a.rmd160});var createHash=require("./browser");algorithms.forEach(function(a){test("test "+a+" against test vectors",function(b){vectors.forEach(function(g,d){var c=new Buffer(g.input,"base64");var e=g[a];var f=createHash(a).update(c).digest("hex");b.equal(f,e,a+"(testVector["+d+"]) == "+e)});encodings.forEach(function(c){vectors.forEach(function(h,e){var d=new Buffer(h.input,"base64").toString(c);var f=h[a];var g=createHash(a).update(d,c).digest("hex");b.equal(g,f,a+"(testVector["+e+"], "+c+") == "+f)})});vectors.forEach(function(h,d){var c=new Buffer(h.input,"base64");var e=h[a];var g=createHash(a);g.end(c);var f=g.read().toString("hex");b.equal(f,e,a+"(testVector["+d+"]) == "+e)});b.end()})});