var test=require("tape");var stringify=require("../");test("replace root",function(b){b.plan(1);var c={a:1,b:2,c:false};var a=function(d,e){return"one"};b.equal(stringify(c,{replacer:a}),'"one"')});test("replace numbers",function(b){b.plan(1);var c={a:1,b:2,c:false};var a=function(d,e){if(e===1){return"one"}if(e===2){return"two"}return e};b.equal(stringify(c,{replacer:a}),'{"a":"one","b":"two","c":false}')});test("replace with object",function(b){b.plan(1);var c={a:1,b:2,c:false};var a=function(d,e){if(d==="b"){return{d:1}}if(e===1){return"one"}return e};b.equal(stringify(c,{replacer:a}),'{"a":"one","b":{"d":"one"},"c":false}')});test("replace with undefined",function(b){b.plan(1);var c={a:1,b:2,c:false};var a=function(d,e){if(e===false){return}return e};b.equal(stringify(c,{replacer:a}),'{"a":1,"b":2}')});test("replace with array",function(b){b.plan(1);var c={a:1,b:2,c:false};var a=function(d,e){if(d==="b"){return["one","two"]}return e};b.equal(stringify(c,{replacer:a}),'{"a":1,"b":["one","two"],"c":false}')});test("replace array item",function(b){b.plan(1);var c={a:1,b:2,c:[1,2]};var a=function(d,e){if(e===1){return"one"}if(e===2){return"two"}return e};b.equal(stringify(c,{replacer:a}),'{"a":"one","b":"two","c":["one","two"]}')});