;"use strict";var should=require("should");var tokens=require("./");var re=/<%=\s*[^>]+%>/g;var pretty=function(a){return require("js-beautify").html(a,{indent_char:" ",indent_size:2})};describe("preserve tokens",function(){var b=/__ID.{5}__\n__ID.{5}__\n__ID.{5}__/;var a=/<%=\s*[^>]+%>/g;it("should (e.g. shouldn't, but will) mangle tokens in the given string",function(){var c=pretty("<ul><li><%= name %></li></ul>");c.should.equal("<ul>\n  <li>\n    <%=n ame %>\n  </li>\n</ul>")});it("should preserve tokens in the given string",function(){var c=tokens.after(pretty(tokens.before("<ul><li><%= name %></li></ul>",a)));c.should.equal("<ul>\n  <li><%= name %></li>\n</ul>")});describe(".before()",function(){it("should replace matches with placeholder tokens:",function(){tokens.before("<%= a %>\n<%= b %>\n<%= c %>",a).should.match(b)})});describe("tokens.after()",function(){it("should replace placeholder tokens with original values:",function(){var c=tokens.before("<%= a %>\n<%= b %>\n<%= c %>",a);c.should.match(b);tokens.after(c).should.equal("<%= a %>\n<%= b %>\n<%= c %>")})})});