var assert=require("assert");var wordwrap=require("wordwrap");var fs=require("fs");var idleness=fs.readFileSync(__dirname+"/idleness.txt","utf8");exports.stop80=function(){var a=wordwrap(80)(idleness).split(/\n/);var b=idleness.split(/\s+/);a.forEach(function(c){assert.ok(c.length<=80,"line > 80 columns");var d=c.match(/\S/)?c.split(/\s+/):[];assert.deepEqual(d,b.splice(0,d.length))})};exports.start20stop60=function(){var a=wordwrap(20,100)(idleness).split(/\n/);var b=idleness.split(/\s+/);a.forEach(function(c){assert.ok(c.length<=100,"line > 100 columns");var d=c.split(/\s+/).filter(function(e){return e.match(/\S/)});assert.deepEqual(d,b.splice(0,d.length));assert.deepEqual(c.slice(0,20),new Array(20+1).join(" "))})};