function GetSlideAngle(b,a){return Math.atan2(a,b)*180/Math.PI}function GetSlideDirection(d,b,g,f){var c=b-f;var e=g-d;var a=0;var h=GetSlideAngle(e,c);if(h>=45&&h<135){a=1}else{if(h>=-135&&h<-45){a=2}else{if((h>=150&&h<=180)||(h>=-180&&h<-150)){if(Math.abs(e)>5){a=3}}else{if(h>=-60&&h<60){if(Math.abs(e)>5){a=4}}}}}console.log("result: "+a);return a};